#pragma config(Sensor, S1,     touchSensor,    sensorEV3_Touch)
#pragma config(Sensor, S4,     touchSensor2,    sensorEV3_Touch)
#pragma config(Motor,  motorB,          rightMotor,    tmotorEV3_Large, PIDControl, driveRight, encoder)
#pragma config(Motor,  motorC,          leftMotor,     tmotorEV3_Large, PIDControl, driveLeft, encoder)

//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//


task main()
{
	int R = random(100);
	int L = random(100);

	while(true){
		R = random(40);
		L = random(40);
	setMotor(motorB, L);
	setMotor(motorC, R);
	//wait(2500, milliseconds);
	//stopAllMotors();
	if (SensorValue[touchSensor])
	{
		displayCenteredBigTextLine(4, "Pressed");
		setLEDColor(ledRed);

	}
	sleep(100);
}
}
