/**
 * backs up for 2 seconds, then turns right at a random angle
 */
void turnRight() {
	displayCenteredBigTextLine(4, "Back");
	setLEDColor(ledOff);
	setMotor(motorC, -50);
	setMotor(motorB, -50);
	sleep(2000);
	sharpRight();
	/*setMotor(motorC, 10);
	setMotor(motorB, random(100));
	sleep(random(3000) + 500); //3 seconds good?
	*/
}

/**
 * backs up for 2 seconds, then turns left at a random angle
 */
void turnLeft() {
	displayCenteredBigTextLine(4, "Back");
	setLEDColor(ledOff);
	setMotor(motorB, -50);
	setMotor(motorC, -50);
	sleep(2000);
	sharpLeft();
	/*setMotor(motorB, 10);
	setMotor(motorC, random(80) + 20);
	sleep(random(3000) + 500);*/
}

/**
 * backs up, goes left or right randomely at a random angle
 */
void backUp() {
	displayCenteredBigTextLine(4, "Back");
	setLEDColor(ledOff);
	setMotor(motorB, -50);
	setMotor(motorC, -50);
	sleep(2000);
	if (random(1)) //0 or 1, true or false... maybe?
	{
		sharpRight();
	}
	else {
		sharpLeft();
	}
	sleep(random(3000) + 500);
}

void travelLeft() {
	displayCenteredBigTextLine(4, "Left");
	setLEDColor(ledGreen);
	setMotor(motorB, 50);
	setMotor(motorC, 60);
	sleep(random(3000) + 500);
}

void travelRight() {
	displayCenteredBigTextLine(4, "Right");
	setLEDColor(ledRed);
	setMotor(motorB, 60);
	setMotor(motorC, 50);
	sleep(random(3000) + 500);
}

void travelForward() {
	displayCenteredBigTextLine(4, "Straight");
	setLEDColor(ledOff);
	setMotor(motorB, 60);
	setMotor(motorC, 60);
	sleep(random(2000) + 500);
}

void sharpRight() {
	displayCenteredBigTextLine(4, "Right");
	setLEDColor(ledRed);
	setMotor(motorB, 50);
	setMotor(motorC, 0);
	sleep(400);
}

void sharpLeft() {
	displayCenteredBigTextLine(4, "Right");
	setLEDColor(ledGreen);
	setMotor(motorB, 0);
	setMotor(motorC, 50);
	sleep(400);
}
